      @green: #a6e3a1;
      @teal: #94e2d5;
      @sky: #89dceb;
      @sapphire: #74c7ec;
      @blue: #89b4fa;
      @lavender: #b4befe;
      @text: #cdd6f4;
      @subtext1: #bac2de;
      @subtext0: #a6adc8;
      @overlay2: #9399b2;
      @overlay1: #7f849c;
      @overlay0: #6c7086;
      @surface2: #585b70;
      @surface1: #45475a;
      @surface0: #313244;
      @base: #1e1e2e;
      @mantle: #181825;
      @crust: #11111b;
    };
  };

  #catppuccin(@lookup, @accent) {
    @rosewater: @catppuccin[@@lookup][@rosewater];
    @flamingo: @catppuccin[@@lookup][@flamingo];
    @pink: @catppuccin[@@lookup][@pink];
    @mauve: @catppuccin[@@lookup][@mauve];
    @red: @catppuccin[@@lookup][@red];
    @maroon: @catppuccin[@@lookup][@maroon];
    @peach: @catppuccin[@@lookup][@peach];
    @yellow: @catppuccin[@@lookup][@yellow];
    @green: @catppuccin[@@lookup][@green];
    @teal: @catppuccin[@@lookup][@teal];
    @sky: @catppuccin[@@lookup][@sky];
    @sapphire: @catppuccin[@@lookup][@sapphire];
    @blue: @catppuccin[@@lookup][@blue];
    @lavender: @catppuccin[@@lookup][@lavender];
    @text: @catppuccin[@@lookup][@text];
    @subtext1: @catppuccin[@@lookup][@subtext1];
    @subtext0: @catppuccin[@@lookup][@subtext0];
    @overlay2: @catppuccin[@@lookup][@overlay2];
    @overlay1: @catppuccin[@@lookup][@overlay1];
    @overlay0: @catppuccin[@@lookup][@overlay0];
    @surface2: @catppuccin[@@lookup][@surface2];
    @surface1: @catppuccin[@@lookup][@surface1];
    @surface0: @catppuccin[@@lookup][@surface0];
    @base: @catppuccin[@@lookup][@base];
    @mantle: @catppuccin[@@lookup][@mantle];
    @crust: @catppuccin[@@lookup][@crust];
    @accent-color: @catppuccin[@@lookup][@@accent];

    body {
      background-color: @mantle;
      color: @text;
      font-family: sans-serif;
    }
    a {
      color: @accent;
    }

    /* Web UI */
    textarea,
    select,
    input[type="submit"] {
      background-color: @base;
      color: @text;
      border: 1px solid @overlay0;
      border-radius: 4px;
    }
    textarea:focus,
    select:focus,
    input[type="submit"]:focus {
      border-color: @accent;
      outline: 1px solid @overlay0;
    }
    input[type="submit"]:hover {
      background-color: @mantle;
    }

    /* Syntax-highlighted code */
    pre {
      background-color: @base !important;
    }
    /* Main text */
    span[style="color:#657b83;"] {
      color: @text !important;
    }
    /**/
    span[style="color:#859900;"] {
      color: @yellow !important;
    }
    /**/
    span[style="color:#268bd2;"] {
      color: @sky !important;
    }
    /* Comments */
    span[style="color:#93a1a1;"] {
      color: @overlay1 !important;
    }
    /**/
    span[style="color:#b58900;"] {
      color: @yellow !important;
    }
    /**/
    span[style="color:#586e75;"] {
      color: @mauve !important;
    }
    /* Quotes */
    span[style="color:#839496;"] {
      color: @green !important;
    }
    /* String literals */
    span[style="color:#2aa198;"] {
      color: @green !important;
    }
    /**/
    span[style="color:#6c71c4;"] {
      color: @teal !important;
    }
    /* self/this */
    span[style="color:#d33682;"] {
      color: @red !important;
    }
    /* Constants */
    span[style="color:#cb4b16;"] {
      color: @peach !important;
    }
    /* String escape sequences */
    span[style="color:#dc322f;"] {
      color: @maroon !important;
    }
  }
}

/* ==UserStyle==
