      --body-bg: @base;
      --bg-glassy: @mantle;
      --bg-1: @surface1;
      --bg-2: @surface0;
      --bg-02: @crust;
      --bg-04: @mantle;
      --bg-green: @green;
      --bg-blue: @blue;
      --bg-red: @red;
      --bg-favicon: @surface1;
      --bg-form-control-selected: @surface2;
      --bg-form-control-unselected: @surface0;
      --form-control-selected: @blue;
      --form-control-hover: @lavender;
