    div.ui.borderless.pagination.menu,
    div.ui.two.item.tabable.menu,
    div.ui.secondary.tiny.pointing.borderless.menu.center.grid.repos-filter,
    div.header-wrapper,
    div.ui.attached.segment.df.ac.sb.py-2.commit-header-row.fw,
