@-moz-document regexp("https:\/\/(gist\.)*github\.com(?!\/(marketplace|organizations\/plan)).*") {

  [data-color-mode="auto"][data-dark-theme*="light"],
  [data-color-mode="light"][data-light-theme*="dark"],
  [data-color-mode="light"][data-light-theme*="light"] {
    #catppuccin(@lightFlavor, @accentColor);
  }
  [data-color-mode="dark"][data-dark-theme*="dark"],
  [data-color-mode="dark"][data-dark-theme*="light"],
  [data-color-mode="auto"][data-dark-theme*="dark"] {
    #catppuccin(@darkFlavor, @accentColor);
  }

  #coloredButton(@name, @color) {
    --color-btn-@{name}-text: @base;
    --color-btn-@{name}-bg: @color;
    --color-btn-@{name}-border: transparent;
    --color-btn-@{name}-shadow: 0 0 transparent;
    --color-btn-@{name}-inset-shadow: 0 0 transparent;
    --color-btn-@{name}-hover-bg: mix(@color, @text, 30%);
    --color-btn-@{name}-hover-border: rgba(240, 246, 252, 0.1);
    --color-btn-@{name}-selected-bg: mix(@color, @text, 50%);
    --color-btn-@{name}-selected-shadow: 0 0 transparent;
    --color-btn-@{name}-disabled-text: @crust;
    --color-btn-@{name}-disabled-bg: mix(@color, @base, 60%);
    --color-btn-@{name}-disabled-border: transparent;
    --color-btn-@{name}-focus-bg: #238636;
    --color-btn-@{name}-focus-border: rgba(240, 246, 252, 0.1);
    --color-btn-@{name}-focus-shadow: 0 0 0 3px rgba(46, 164, 79, 0.4);
    --color-btn-@{name}-icon: @base;
    --color-btn-@{name}-counter-bg: rgba(255, 255, 255, 0.2);
  }

  #catppuccin(@lookup, @accent) {
    @rosewater: @catppuccin[@@lookup][@rosewater];
    @flamingo: @catppuccin[@@lookup][@flamingo];
    @pink: @catppuccin[@@lookup][@pink];
