    /* Buttons */
    .btn-danger,
    .Button--danger {
      background: var(--color-btn-danger-bg)
    }

    /* Create PR button & New branch button */
    .iRoQzU, .ifkUYV, .bOZDzN {
      background-color: @green;
      color: @base;
    }

    /* New branch dialog */
    .jenxrv {
      background-color: @mantle;
      color: @text;
    }

    /* Branch drop down */
    .fHiIQb {
      background-color: @surface0;
    }

